package org.example.bookstoremongo;

import lombok.RequiredArgsConstructor;
import org.example.bookstoremongo.ds.Author;
import org.example.bookstoremongo.ds.Book;
import org.example.bookstoremongo.repository.BookRepository;
import org.springframework.boot.ApplicationRunner;
import org.springframework.boot.SpringApplication;
import org.springframework.boot.autoconfigure.SpringBootApplication;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Profile;

import java.time.LocalDate;
import java.util.List;

@SpringBootApplication
@RequiredArgsConstructor
public class BookstoreMongoApplication {
    private final BookRepository bookRepository;

    @Bean @Profile("dev")
    public ApplicationRunner runner(){
        return args -> {
            List.of(new Book(null,"Moon Pig",30.5,"https://images.unsplash.com/photo-1716125581956-81ca1b3f6ad6?w=500&auto=format&fit=crop&q=60&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxlZGl0b3JpYWwtZmVlZHwyfHx8ZW58MHx8fHx8", LocalDate.now(),new Author("John","William")),
                    new Book(null,"A Christmas Carol",30.5,"data:image/jpeg;base64,", LocalDate.now(),new Author("charles","Dickens")),
                    new Book(null,"Adventures of Huckleberry",30.5,"data:image/jpeg;base64,", LocalDate.now(),new Author("Mark","Twain"))
                    ,
                    new Book(null,"Mrs.Mark Twain",30.5,"data:image/jpeg;base64,", LocalDate.now(),new Author("Olivia ","Langdon Clemens"))).forEach(bookRepository::save);
        };
    }

    public static void main(String[] args) {
        SpringApplication.run(BookstoreMongoApplication.class, args);
    }

}
